customCSS.value:
:root {
  --animation-dimension: 120px;
  --animation-start-color: #71e171;
  --animation-midway-color: #67d067;
  --animation-finish-color: #59b259;
}

.checkmark {
  width: var(--animation-dimension);
  height: var(--animation-dimension);
  border-radius: 50%;
  display: block;
  stroke-width: 5;
  stroke: #ffffff;
  stroke-miterlimit: 10;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
  animation: fill 0.4s ease-in-out 0.4s forwards,
    scale 0.3s ease-in-out 0.9s both;
}

.checkmarkCircle {
  stroke-dasharray: 166;
  stroke-dashoffset: 166;
  stroke-width: 5;
  stroke-miterlimit: 10;
  stroke: var(--animation-finish-color);
  fill: none;
  animation: stroke 0.6s cubic-bezier(0.65, 0, 0.45, 1) forwards,
    colorChange 1.2s ease-in-out forwards;
}

.checkmarkCheck {
  transform-origin: 50% 50%;
  stroke-dasharray: 48;
  stroke-dashoffset: 48;
  animation: stroke 0.3s cubic-bezier(0.65, 0, 0.45, 1) 0.8s forwards,
    colorChange 0.6s ease-in-out 0.8s forwards;
}

@keyframes colorChange {
  0% {
    stroke: var(--animation-start-color);
  }
  50% {
    stroke: var(--animation-midway-color);
  } 
  100% {
    stroke: var(--animation-finish-color);
  }
}

@keyframes stroke {
  100% {
    stroke-dashoffset: 0;
  }
}
@keyframes scale {
  0%,
  100% {
    transform: none;
  }
  50% {
    transform: scale3d(1.1, 1.1, 1);
  }
  70% {
    transform: scale3d(0.98, 0.98, 1);
  }
  85% {
    transform: scale3d(1.02, 1.02, 1);
  }
}
@keyframes fill {
  100% {
    box-shadow: inset 0px 0px 0px 0px var(--animation-finish-color);
  }
}

.loading-spinner-container {
    display: flex;
    justify-content: center; /* Centers horizontally */
    align-items: center; /* Centers vertically */
    margin-bottom: 25px;
}

 .large-spinner {
            width: 5rem;   /* Adjusts the width of the spinner */
            height: 5rem;  /* Adjusts the height of the spinner */
            border-width: 0.5rem; /* Makes the spinner border thicker */
        }

customScript.value:
var checkInterval = setInterval(function () {
    var feedbackElement = document.getElementById('feedback');
    if (feedbackElement) {
        // Element found, clear the interval
        console.log("element found");
        clearInterval(checkInterval);
        console.log("clicking button");
        document.getElementById('continueBtn').click();
    }
}, 1000); // Check every 1000 milliseconds (1 second)


setTimeout(function () {

    let clientId = '{{local.k4mns41mib.payload.output.DVClientId}}';
    let clientSecret = '{{local.k4mns41mib.payload.output.DVClientSecret}}';
    let unencodedHeader = clientId + ":" + clientSecret;
    console.log(unencodedHeader); // Logs the unencoded header

    let encodedHeader = btoa(unencodedHeader);
    console.log(encodedHeader); // Logs the base64 encoded header

    const authHeaderElement = document.createElement('input');
    const formElement = document.getElementById('authHeaderForm');
    authHeaderElement.type = 'hidden';
    authHeaderElement.id = 'authHeader';

    if (authHeaderElement) {
        console.log('setting header value');
        formElement.appendChild(authHeaderElement);
        authHeaderElement.value = encodedHeader;
        // To log the value of 'authHeader' element
        let authHeaderValue = authHeaderElement.value;
        console.log('Auth Header Value: ' + authHeaderValue);
    } else {
        console.log('No Auth Header Element!')
    }


    console.log("clicking button");
    document.getElementById('submitBtn').click();
}, 1000);

