// Write your code here
// Supported language: Javascript 
module.exports = a = async ({ params }) => {
	const data = params.variables;
	const filtered = {};

	Object.keys(data).forEach(key => {
		if (key.startsWith("gv") && data[key].context === "company" && data[key].type === "boolean") {
			filtered[key] = data[key]; // Keep the original data structure
		}
	});

	// Transform the filtered object into an array of { name, value } objects
	const resultArray = Object.keys(filtered).map(key => ({
		name: key.replace("##SK##company", ""), // Strip "##SK##company" from the key
		value: key === "gv-p1PasswordPolicy" ? null : filtered[key].value
	}));

	return {
		'filteredVariables': filtered,
		'resultArray': resultArray
	};
}

