customScript.value:
const focusOnFirstInputElement = () => {
  document.getElementById("username").focus();
};

// Get references to the input fields and the submit button
const usernameInput = document.getElementById('username');
const passwordInput = document.getElementById('password');
const submitBtn = document.getElementById('btnSignIn');
let authMethod = 'password';
const togglePasswordless = document.getElementById('btnTogglePasswordless');
const passwordlessSubmit = document.getElementById('btnPasswordless');
const passwordElement = document.getElementById('passwordContainer');
const toggleIcon = document.getElementById('toggleIcon');
const toggleText = document.getElementById('toggleText');

togglePasswordless.addEventListener('click', function () {
  console.log(authMethod);
  if (authMethod === 'password') {
    passwordElement.style.display = 'none';
    passwordlessSubmit.style.display = 'block';
    submitBtn.style.display = 'none';
    toggleText.textContent = 'Sign in With Password';
    toggleIcon.style.display = 'none';
    authMethod = 'paswordless';
  } else {
    console.log('running else if');
    passwordElement.style.display = 'block';
    passwordlessSubmit.style.display = 'none';
    submitBtn.style.display = 'block';
    authMethod = 'password';
    toggleText.textContent = 'Sign in With Passwordless';
    toggleIcon.style.display = 'inline';
  }
});

if (authMethod === 'password') {
  // Add event listeners for input events on both fields
  usernameInput.addEventListener('input', checkInputsPassword);
  passwordInput.addEventListener('input', checkInputsPassword);
} else if (authMethod === 'passwordless'); {
  usernameInput.addEventListener('input', checkInputsPasswordless);
}


function checkInputsPassword() {
  if (usernameInput.value && passwordInput.value) {
    // If both fields have values, remove the disabled class from the submit button
    submitBtn.classList.remove('disabled');
  } else {
    // Optional: Add the class back if either field is empty 
    if (!submitBtn.classList.contains('disabled')) {
      submitBtn.classList.add('disabled');
    }
  }
}


function checkInputsPasswordless() {
  if (usernameInput.value) {
    // If both fields have values, remove the disabled class from the submit button
    btnPasswordless.classList.remove('disabled');
  } else {
    // Optional: Add the class back if either field is empty 
    if (!btnPasswordless.classList.contains('disabled')) {
      btnPasswordless.classList.add('disabled');
    }
  }
}




// Get the input field
var input = document.getElementById("password");

// Get the warning text
var text = document.getElementById("passwordFeedback");

// When the user presses any key on the keyboard, run the function
input.addEventListener("keyup", function (event) {

  // If "caps lock" is pressed, display the warning text
  if (event.getModifierState("CapsLock")) {
    text.style.display = "block";
  } else {
    text.style.display = "none"
  }
});

// Define which login options are enabled
const loginOptions = {
  google: { enabled: {{global.company.variables.gv-googleLogin}}, backgroundColor: '#F2F2F2', color: '#1F1F1F' },
  facebook: { enabled: {{global.company.variables.gv-facebookLogin}}, backgroundColor: '#1877F2', color: '#FFFFFF' },
  azure: { enabled: {{global.company.variables.gv-azureLogin}}, backgroundColor: '#2F2F2F', color: '#FFFFFF' },
  github: { enabled: {{global.company.variables.gv-githubLogin}}, backgroundColor: '#FFFFFF', color: '#2F2F2F' },
};

// Function to create a button element with color, unique ID, and setup to click another button on click
function createButton(loginType, svgContent, backgroundColor, color) {
  const button = document.createElement('button');
  button.setAttribute('type', 'submit');
  button.classList.add('btn', 'mb-3');
  // Create a unique ID for each button based on the loginType
  button.id = `btn${loginType}`;
  button.dataset.id = "button";
  button.innerHTML = svgContent;
  button.style.backgroundColor = backgroundColor;
  button.style.color = color;

  if (loginType === 'google') {
    button.style.border = 'none';
  } else {
    button.style.border = '1px solid';
  }

  // Add click event listener to the button
  button.addEventListener('click', function() {
    // Log to console which button was clicked (for demonstration)
    console.log(`${loginType} button clicked`);
    // Target the "submit" button related to this button
    const submitButtonId = `${button.id}Submit`;
    const submitButton = document.getElementById(submitButtonId);
    if (submitButton) {
      submitButton.click(); // Programmatically click the submit button
    } else {
      console.log('Submit button not found:', submitButtonId);
    }
  });

  return button;
}

// SVG content for each button
const svgs = {
  google: `<svg width="21" height="21" viewBox="0 0 21 21" fill="none" xmlns="http://www.w3.org/2000/svg"><g clip-path="url(#clip0_1122_1133)"><path d="M20.3052 10.4902C20.3052 9.8104 20.2501 9.12696 20.1325 8.45821H10.7002V12.309H16.1016C15.8775 13.551 15.1573 14.6496 14.1027 15.3478V17.8464H17.3252C19.2176 16.1047 20.3052 13.5326 20.3052 10.4902Z" fill="#4285F4" /><path d="M10.6999 20.2605C13.397 20.2605 15.6714 19.375 17.3286 17.8464L14.1061 15.3478C13.2096 15.9577 12.0521 16.3031 10.7036 16.3031C8.09474 16.3031 5.88272 14.5431 5.08904 12.1767H1.76367V14.7525C3.46127 18.1293 6.91892 20.2605 10.6999 20.2605Z" fill="#34A853" /><path d="M5.08564 12.1767C4.66676 10.9348 4.66676 9.58993 5.08564 8.34796V5.77218H1.76395C0.345611 8.59783 0.345611 11.9269 1.76395 14.7525L5.08564 12.1767Z" fill="#FBBC04" /><path d="M10.6999 4.21788C12.1256 4.19583 13.5035 4.7323 14.536 5.71705L17.3911 2.86201C15.5833 1.16441 13.1838 0.231105 10.6999 0.260501C6.91892 0.260501 3.46126 2.39168 1.76367 5.77217L5.08537 8.34795C5.87537 5.97794 8.09106 4.21788 10.6999 4.21788Z" fill="#EA4335" /></g><defs><clipPath id="clip0_1122_1133"><rect width="20" height="20" fill="white" transform="translate(0.5 0.259827)" /></clipPath></defs></svg> Sign in with Google`,
  facebook: `<svg x="0px" y="0px" viewBox="0 0 100 100" width="21" height="21"><rect x="35.5" y="10.3" width="52.8" height="89.7" style="fill: rgb(255, 255, 255);"></rect><path d="M94.5,0h-89C2.5,0,0,2.5,0,5.5v89c0,3,2.5,5.5,5.5,5.5h47.9V61.3h-13V46.2h13V35.1c0-12.9,7.9-19.9,19.4-19.9 c5.5,0,10.3,0.4,11.6,0.6v13.5l-8,0c-6.3,0-7.5,3-7.5,7.3v9.6h14.9L82,61.3H69V100h25.5c3,0,5.5-2.5,5.5-5.5v-89 C100,2.5,97.5,0,94.5,0z" style="fill: rgb(24, 119, 242);"></path></svg> Sign in with Facebook`,
  azure: `<svg x="0px" y="0px" viewBox="0 0 100 100" width="21" height="21"><rect width="47.37" height="47.37" style="fill: rgb(242, 80, 34);"></rect><rect y="52.63" width="47.37" height="47.37" style="fill: rgb(0, 164, 239);"></rect><rect x="52.63" width="47.37" height="47.37" style="fill: rgb(127, 186, 0);"></rect><rect x="52.63" y="52.63" width="47.37" height="47.37" style="fill: rgb(255, 185, 0);"></rect></svg> Sign in with Microsoft`,
  github: `<svg width="21" height="21" viewBox="0 0 16 16" xmlns="http://www.w3.org/2000/svg" fill="#000000"><rect width="16" height="16" fill="#FFFFFF"></rect><path fill-rule="evenodd" clip-rule="evenodd" d="M7.976 0A7.977 7.977 0 0 0 0 7.976c0 3.522 2.3 6.507 5.431 7.584.392.049.538-.196.538-.392v-1.37c-2.201.49-2.69-1.076-2.69-1.076-.343-.93-.881-1.175-.881-1.175-.734-.489.048-.489.048-.489.783.049 1.224.832 1.224.832.734 1.223 1.859.88 2.3.685.048-.538.293-.88.489-1.076-1.762-.196-3.621-.881-3.621-3.964 0-.88.293-1.566.832-2.153-.05-.147-.343-.978.098-2.055 0 0 .685-.196 2.201.832.636-.196 1.322-.245 2.007-.245s1.37.098 2.006.245c1.517-1.027 2.202-.832 2.202-.832.44 1.077.146 1.908.097 2.104a3.16 3.16 0 0 1 .832 2.153c0 3.083-1.86 3.719-3.62 3.915.293.244.538.733.538 1.467v2.202c0 .196.146.44.538.392A7.984 7.984 0 0 0 16 7.976C15.951 3.572 12.38 0 7.976 0z"/></svg> Sign in with Github`,
};


// Container where buttons will be added
const container = document.getElementById('authButtonsContainer');

// Check if any login options are enabled
const anyLoginEnabled = Object.values(loginOptions).some(option => option.enabled);

if (anyLoginEnabled) {
  // Create an hr element
  const hrTextDiv = document.createElement('div');
  hrTextDiv.className = 'hr-text';
  hrTextDiv.innerHTML = '<span>Or Sign In With</span>';

  // Insert the hr element above the container
  container.parentNode.insertBefore(hrTextDiv, container);
}

// Iterate through the login options and create buttons for the enabled ones
Object.keys(loginOptions).forEach(key => {
  if (loginOptions[key].enabled) { // Check if the login option is enabled
    const option = loginOptions[key];
    const button = createButton(key, svgs[key], option.backgroundColor, option.color);
    container.appendChild(button);
  }
});

function makePasswordToggle(id) {
  var container = document.getElementById(id);
  var password = container.getElementsByTagName("input")[0];
  var toggler = document.createElement("button");
  toggler.setAttribute("type", "button");
  toggler.setAttribute("aria-label", "Show/Hide Password");
  toggler.className = "btn mdi mdi-eye-off-outline position-absolute end-0 top-50 translate-middle-y";
  container.appendChild(toggler);

  function showHidePassword() {
    if (password.type == "password") {
      password.setAttribute("type", "text");
      toggler.classList.add("mdi-eye-outline");
      toggler.classList.remove("mdi-eye-off-outline");
    } else {
      toggler.classList.add("mdi-eye-off-outline");
      toggler.classList.remove("mdi-eye-outline");
      password.setAttribute("type", "password");
    }
    password.focus();
  };

  toggler.addEventListener("click", showHidePassword);
}

