// test2.js

module.exports =  ({ params }) => {
    const lastChangeDate = params.lastChangeDate;
    const status = params.status;
    const passwordPolicyDays = params.passwordPolicyDays;

    if (params.status !== "PASSWORD_EXPIRED") {
      console.log(params.status);
    } else {
      return { 'Status': status };
    }
    console.log("Your Password is expired, please update");


    function getDaysUntilDate(lastChangeDate) {
      const daysSinceChange = new Date(lastChangeDate);
      const todayDate = new Date();
      console.log("today date:", todayDate);
      const timeDifference = daysSinceChange.getTime() - todayDate.getTime();
      const daysDifference = Math.floor(timeDifference / (1000 * 3600 * 24));
      console.log("Number of days until", lastChangeDate, ":", daysDifference);
      const passwordDays =  passwordPolicyDays - daysDifference ;
      console.log("Number of Password days until", lastChangeDate, ":", passwordDays);
      return passwordDays;
    }

    const daysUntilExpiredtDate = getDaysUntilDate(lastChangeDate);
    return { 'daysUntilExpire': daysUntilExpiredtDate };
  };

