customScript.value:
const WINDOW_CLOSE_DURATION = 10;

function autoCloseWindow(count) {

  let label = document.getElementById("timerMsg");
  label.innerHTML = `This window will attempt to close in <br/><strong>${count}</strong> seconds.`

  const countdown = setInterval(function () {
    count--;

    const secondsText = count === 1 ? "second" : "seconds";
    label.innerHTML = `This window will attempt to close in <br/><strong>${count}</strong> ${secondsText}.`;

    if (count <= 0) {
      clearInterval(countdown);
      window.close();
      // Msg in the event the window cannot be closed programmatically 
      label.innerHTML = `You may now safely close this window.`;
    }
  }, 500);
}

function start() {
  // Close window in 10 seconds
  autoCloseWindow(WINDOW_CLOSE_DURATION);
}

if (document.readyState === "loading") {
  // Loading hasn't finished yet
  document.addEventListener("DOMContentLoaded", start);
} else {
  // `DOMContentLoaded` has already fired
  start();
}

