function generateJson(question1, answer1, question2, answer2, question3, answer3) {
    const data = {};

    if (question1 !== "" && answer1 !== "") {
        data["question1"] = question1;
        data["answer1"] = answer1;
    }

    if (question2 !== "" && answer2 !== "") {
        data["question2"] = question2;
        data["answer2"] = answer2;
    }

    if (question3 !== "" && answer3 !== "") {
        data["question3"] = question3;
        data["answer3"] = answer3;
    }

    const jsonObject = JSON.stringify(data, null, 2);
	return jsonObject;
}
const resultJson = generateJson(
    params.question1, params.answer1,
    params.question2, params.answer2, // Empty strings won't be added to the output JSON
    params.question3, params.answer3
);

return {'resultJson': resultJson};

}

