code.value:
module.exports = a = async ({ params }) => {
 const inputObj = params.securityQA;
  const questionSet = [];

   Object.keys(inputObj)
    .filter((key) => key.startsWith("question"))
    .forEach((key, index) => {
      const questionNumber = key.replace("question", "");

      // Create a new object with the corresponding question, answer, and questionIndex
      const questionObject = {
        question: inputObj[key],
        answer: inputObj[`answer${questionNumber}`],
        questionIndex: index + 1,
      };

      // Push the new object to the result array
      questionSet.push(questionObject);
    });

  // Return the resulting JSON array
  return {'questionSet' : questionSet}
}

