  const inputObj = params.questionSet;
  const inputQuestion = params.inputQuestion;
  const inputAnswer = params.inputAnswer;

  // Find the question object based on the input question
  const questionObj = inputObj.find(obj => obj.question === inputQuestion);

  if (questionObj) {
    const actualAnswer = questionObj.answer;

    // Convert both answers to lowercase for case-insensitive comparison
    const lowercasedActualAnswer = actualAnswer.toLowerCase();
    const lowercasedInputAnswer = inputAnswer.trim().toLowerCase(); // Trim leading/trailing spaces

    // Compare the lowercase input answer against the lowercase actual answer
    if (lowercasedActualAnswer === lowercasedInputAnswer)
    {
    return;
    } else
    {
      return false;
    }
  } else {
    return false;
  }
};

