module.exports = a = async ({ params }) => {
	var selected = JSON.parse(params.devices).find(device => {

		return device.id === params.id;
	});

	// Mask email
	const maskEmail = (email) => {
		const [username, domain] = email.split("@");
		const maskedUsername =
			username.charAt(0) + "*".repeat(username.length - 2) + username.slice(-1);
		return maskedUsername + "@" + domain;
	};

	//Mask phone number
	const maskPhone = (phone) => {
		const numericPhoneNumber = phone.replace(/[^\d+]/g, "");
		// Mask the first six digits of the phone number
		const maskedPhoneNumber = "***-***-" + numericPhoneNumber.slice(-4);
		return maskedPhoneNumber;
	};

	let maskedPhone = ""
	let maskedEmail = ""
	if (selected.type == "SMS" || selected.type == "VOICE") {
		maskedPhone = maskPhone(selected.phone);
	} else if (selected.type == "EMAIL") {
		maskedEmail = maskEmail(selected.email);
	}
	let iconClass = "";
	if (selected.type === "SMS") {
		iconClass = "mdi-comment-text-outline";
	} else if (selected.type === "VOICE") {
		iconClass = "mdi-comment-text-outline";
	} else if (selected.type === "EMAIL") {
		iconClass = "mdi-email-outline";
	} else if (selected.type === "TOTP") {
		iconClass = "mdi-cellphone";
	} else if (selected.type === "MOBILE") {
		iconClass = "mdi-cellphone";
	}

	var applicationName = selected?.application?.name || "";
	var deviceName = selected?.model?.marketingName || selected?.nickname;
	return {
		selectedDeviceType: selected.type,
		selectedDeviceEmail: maskedEmail,
		selectedDevicePhone: maskedPhone,
		selectedDeviceAppName: applicationName,
		selectedDeviceName: deviceName,
		selectedDeviceOtpEnabled: selected.otpEnabled,
		selectedDeviceIcon: iconClass

	}
}

