module.exports = a = async ({params}) => {
	var selected = JSON.parse(params.devices).find(device => {

			return device.id === params.id;
	});


	var applicationName =   selected?.application?.name || "";
	var deviceName =  selected?.model?.marketingName || selected?.nickname;
	return {selectedDeviceType: selected.type, 
			selectedDeviceEmail: selected.email,
			selectedDevicePhone: selected.phone,
			selectedDeviceAppName:  applicationName,
			selectedDeviceName:  deviceName,
			selectedDeviceOtpEnabled:  selected.otpEnabled,

			}
}

