module.exports = a = async ({params}) => {
let devices = params.devices;

// Find the device with nickname "Magic Link"
const magicLinkDevice = devices.find(device => device.type === 'MAGIC' && device.nickname === 'Magic Link');

// Extract the id if the device is found
const magicLinkId = magicLinkDevice ? magicLinkDevice.id : null;
const email = magicLinkDevice.email;
if (magicLinkId == params.buttonValue)
{
	return {'email' : email};

} else
{
	return false;
}


}

