/*
 Update the devices array to include a new properties 
 used when rendering device list.
*/

/*
* deviceTypes
* title: Display type for the device
* icon: Icon used on forms
* description: Brief overview of device type
* maxDevices: Number of devices a user can have of that type (-1 = unlimited, or up to policy max)
*/
let deviceTypes = {
	SMS: { title: "Text Message", icon: "mdi-comment-text-outline", description: "Receive a text message with a passcode to authenticate.", maxDevices: -1 },
	VOICE: { title: "Voice", icon: "mdi-phone-outline", description: "Receive a phone call with a passcode to authenticate.", maxDevices: -1 },
	EMAIL: { title: "Email", icon: "mdi-email-outline", description: "Receive an email with a passcode to authenticate.", maxDevices: -1 },
	TOTP: { title: "Authenticator App", icon: "mdi-cellphone", description: "Use an authenticator app to authenticate.", maxDevices: -1 },
	FIDO2: { title: "Passkey", icon: "mdi-fingerprint", description: "Authenticate using the biometrics built into your device.", maxDevices: -1 },
	PLATFORM: { title: "Biometrics", icon: "mdi-fingerprint", description: "Authenticate using the biometrics built into your device.", maxDevices: -1 },
	MOBILE: { title: "Mobile", icon: "mdi-cellphone", description: "Receive a push notification to authenticate.", maxDevices: -1 },
	SECURITY_KEY: { title: "Security Key", icon: "mdi-usb-flash-drive", description: "Use a sercurity key to authenticate", maxDevices: -1 },
	MAGIC: { title: "Magic Link", icon: "mdi-book-open-page-variant", description: "Use magic link to authenticate", maxDevices: -1 }
}

