var counter = 1; // Initialize a counter for unique IDs

document.getElementById('addAnotherBtn').addEventListener('click', function () {
    // Check if the counter is less than or equal to 2 before cloning
    if (counter <= 2) {
        // Clone the select and answer input fields
        var cloneSelectSection = document.querySelector('#selectSection').cloneNode(true);
        var cloneAnswer = document.querySelector('#answerSection').cloneNode(true);

        // Generate unique IDs for the cloned elements
        var newSelectSectionId = 'selectSection' + counter;
        var newSelectQuestionId = 'selectQuestion' + counter;
        var newAnswerId = 'answer' + counter;
        var newRemoveBtnId = 'removeBtn' + counter;

        // Assign new IDs to the cloned elements
        cloneSelectSection.id = newSelectSectionId;

        // Find the "selectQuestion" element inside the cloned "selectSection"
        var clonedSelectQuestion = cloneSelectSection.querySelector('.form-select');

        // Find the "answer" element inside the cloned "selectSection"
        var clonedAnswerInput = cloneAnswer.querySelector('.form-control');

        // Check if the "selectQuestion" element exists inside the cloned "selectSection"
        if (clonedSelectQuestion) {
            // Assign a new ID to the "selectQuestion" element
            clonedSelectQuestion.id = newSelectQuestionId;
        }
        if (clonedAnswerInput) {
        clonedAnswerInput.id = newAnswerId; // Assign new IDs to the cloned answer element
        }

        // Create a "Remove" button
        var removeBtn = document.createElement('button');
        removeBtn.id = newRemoveBtnId;
        removeBtn.className = 'btn btn-danger removeBtn';
        removeBtn.innerHTML = '- Remove Question';

        // Attach an event listener to the "Remove" button
        removeBtn.addEventListener('click', function () {
            // Remove the corresponding cloned container
            cloneSelectSection.parentNode.removeChild(cloneSelectSection);
            cloneAnswer.parentNode.removeChild(cloneAnswer);

            // Decrement the counter by 1
            counter--;

            // Enable the "addAnotherBtn" button after removing a section
            document.getElementById('addAnotherBtn').style.display = 'block';
            document.getElementById('addAnotherBtn').disabled = false;

            // Remove the "Remove" button row
            removeBtnRow.parentNode.removeChild(removeBtnRow);

            // Disable the "remove" button if there are no cloned sections
            if (counter <= 1) {
                document.getElementById('removeSectionBtn').disabled = true;
            }
        });

        // Increment the counter for the next set of cloned elements
        counter++;

        // Clear the values in the cloned fields
        if (clonedSelectQuestion) {
            clonedSelectQuestion.selectedIndex = 0;
        }
        cloneAnswer.value = '';

        // Append the cloned fields above the submit and cancel buttons
        var form = document.getElementById('securityQA');
        form.insertBefore(cloneSelectSection, document.getElementById('addAnotherBtn'));
        form.insertBefore(cloneAnswer, document.getElementById('addAnotherBtn'));

        // Create a new row for the "Remove" button
        var removeBtnRow = document.createElement('div');
        removeBtnRow.className = 'mb-3 row selectBox';
        removeBtnRow.appendChild(removeBtn);
        form.insertBefore(removeBtnRow, document.getElementById('addAnotherBtn'));
    }

    // If counter exceeds 2, you can disable the button or hide it as needed
    if (counter > 2) {
        document.getElementById('addAnotherBtn').style.display = 'none';
    }
});

