const passcode = document.getElementById("verifyCode");
verifyCode.addEventListener("input", myFunction);
function myFunction() {  verifyCode.value === "" ? verifyCode.parentElement.classList.add("placeholder-shown") : verifyCode.parentElement.classList.remove("placeholder-shown") ;}

const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    }
}

const start = () => {
    setFieldFocus("verifyCode")
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

