/*
 Update the devices array to include a new properties 
 used when rendering device list.
*/

/*
* deviceTypes
* title: Display type for the device
* icon: Icon used on forms
* description: Brief overview of device type
* maxDevices: Number of devices a user can have of that type (-1 = unlimited, or up to policy max)
*/
let deviceTypes = {
	SMS: { title: "Text Message", icon: "mdi-comment-text-outline", description: "Receive a text message with a passcode to authenticate.", maxDevices: 1 },
	VOICE: { title: "Voice", icon: "mdi-face-agent", description: "Receive a phone call with a passcode to authenticate.", maxDevices: 1 },
	EMAIL: { title: "Email", icon: "mdi-email-outline", description: "Receive an email with a passcode to authenticate.", maxDevices: 1 },
	TOTP: { title: "Authenticator App", icon: "mdi-cellphone-key", description: "Use an authenticator app to authenticate.", maxDevices: 1 },
	FIDO2: { title: "Passkey", icon: "mdi-fingerprint", description: "Authenticate using the biometrics built into your device.", maxDevices: 1 },
	PLATFORM: { title: "Biometrics", icon: "mdi-fingerprint", description: "Authenticate using the biometrics built into your device.", maxDevices: 1 },
	MOBILE: { title: "Mobile", icon: "mdi-account-key-outline", description: "Receive a push notification to authenticate.", maxDevices: 1 },
	SECURITY_KEY: { title: "Security Key", icon: "mdi-fingerprint", description: "Use a sercurity key to authenticate", maxDevices: 1 },
	MAGIC: { title: "Magic Link", icon: "mdi-auto-fix", description: "Use magic link to authenticate", maxDevices: 1 }
}

// Device types that require masking
const SMS = "SMS";
const VOICE = "VOICE";
const EMAIL = "EMAIL";

// const allowNewIfBlocked = true

// Main entry point
module.exports = a = async ({ params }) => {
	let allowedtypes = params.allowedtypes;
	allowedtypes.push("MAGIC")
	const webAuthnSupport = params.webAuthnSupport;
	const existingDevices = params.existingDevices;

	// If devices is not passed, head to error branch in flow
	if (!allowedtypes || allowedtypes.length === 0) {
		return false;
	}

	let devices = []

	for (const t of allowedtypes) {
		let d = {};
		d.displayType = deviceTypes[t].title || `Unknown Device Type: ${t}`;
		d.displayIcon = deviceTypes[t].icon;
		d.description = deviceTypes[t].description;
		d.type = t;

		devices.push(d);
	}
	// Adding magic link to devices
	// let m = {};
	// m.displayType = deviceTypes["MAGIC"].title;
	// m.displayIcon = deviceTypes["MAGIC"].icon;
	// m.description = deviceTypes["MAGIC"].description;
	// m.type = "MAGIC";
	// devices.push(m);


	// Filter webAuthnSupport devices
	devices = filterWebAuthnSupportedDevices(webAuthnSupport, devices);

	// Iterate over existing devices to determine which device types have
	// been maxed out
	for (const d of existingDevices) {
    // Check if device is of type EMAIL and has a nickname "Magic Link", then treat it as MAGIC
    if (d.type === 'EMAIL' && d.nickname === "Magic Link") {
        const maxDevicesMagic = deviceTypes['MAGIC']?.maxDevices;
        if (maxDevicesMagic !== undefined && maxDevicesMagic > 0 && d.lock?.status !== "LOCKED" && d.block?.status !== "BLOCKED") {
            deviceTypes['MAGIC'].maxDevices--;
        }
    } else {
        // For all other devices, process normally based on their type
        const maxDevices = deviceTypes[d.type]?.maxDevices;
        if (maxDevices !== undefined && maxDevices > 0 && d.lock?.status !== "LOCKED" && d.block?.status !== "BLOCKED") {
            deviceTypes[d.type].maxDevices--;
        }
    }
}

	// Filter devices based on number allowed for type
	devices = devices.filter((d) =>
		deviceTypes[d.type]?.maxDevices === -1 ||
		deviceTypes[d.type]?.maxDevices > 0
	);

	// No devices
	if (devices.length === 0) {
		return false;
	}

	// Sort the devices based on type
	devices.sort((a, b) => a.displayType.localeCompare(b.displayType));

	return { 'devices': devices }
}

// Filter webAuthnSupport devices
const filterWebAuthnSupportedDevices = (webAuthnSupport, devices) => {
	// Filter out devices if not supported by the running browser
	if (webAuthnSupport === "NONE") {
		devices = devices.filter(
			(d) =>
				d.type !== "SECURITY_KEY" &&
				d.type !== "PLATFORM" &&
				d.type !== "TOTP" &&
				d.type !== "OTP"
		);
	} else if (webAuthnSupport === "SECURITY_KEY_ONLY") {
		devices = devices.filter((d) => d.type !== "PLATFORM");
	}
	return devices;
};

