verifyCode.addEventListener("input", showValidationMsg);

 function showValidationMsg() {
    if (verifyCode.value === "") {
        verifyCode.classList.add("is-invalid");
        codevalidationMsg.style.display = "block";
        submitbtn.disabled = true;
    } else {
        verifyCode.classList.remove("is-invalid");
        codevalidationMsg.style.display = "none";
        submitbtn.disabled = false;
    }

}

var errorDiv = document.getElementById('error-wrap');
if(window.addEventListener) {
   // Normal browsers
   errorDiv.addEventListener('DOMSubtreeModified', contentChanged, false);
} else
   if(window.attachEvent) {
      // IE
      errorDiv.attachEvent('DOMSubtreeModified', contentChanged);
   }

function contentChanged() {
   let errorMsg  = document.getElementById('error-msg');
   if (errorMsg) {
      errorDiv.classList.add("feedback");
       if (errorMsg.innerText === "OTP Resent") {
            errorMsg.classList.add("feedback--alert");
            errorMsg.classList.add("picon-alert");
            errorMsg.classList.remove("feedback--error");
            errorMsg.classList.remove("picon-error");
       }else{
            errorMsg.classList.remove("feedback--alert");
            errorMsg.classList.remove("picon-alert");
            errorMsg.classList.add("feedback--error");
            errorMsg.classList.add("picon-error");
       }
   }else{
          errorDiv.classList.remove("feedback");
       }
}


// If the page is still loading, wait for it to finish before calling the function
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded");
    if (selectedLang.toLowerCase() === 'es') {
        langSpanishSelected();
    } else {
        langEnglishSelected();
    }
} else {
    // If the page has already finished loading, call the function immediately

    if (selectedLang.toLowerCase() === 'es') {
        langSpanishSelected();
    } else {
        langEnglishSelected();
    }
}

// English language selected 
function langEnglishSelected() {
	// Selected language tick mark
	document.getElementById("btnCancel").textContent = "CANCEL";
    document.getElementById("lblTitle").textContent = "SMS pairing";
    document.getElementById("lblSubTitle").textContent = "Put in the security code you got to complete SMS pairing.";
    document.getElementById("codevalidationMsg").textContent = "Please enter security code";
    document.getElementById("lblSMSSentTo").textContent = "SMS sent to:";
    document.getElementById("resend").textContent = "RESEND CODE";
    document.getElementById("submitBtn").textContent = "VERIFY";
}

// Spanish language selected
function langSpanishSelected() {
	// Selected language tick mark
   document.getElementById("btnCancel").textContent = "CANCELAR";
    document.getElementById("lblTitle").textContent = "Emparejamiento por SMS";
    document.getElementById("lblSubTitle").textContent = "Ingrese el código de seguridad que obtuvo para completar el emparejamiento por SMS.";
    document.getElementById("codevalidationMsg").textContent = "Ingrese el código de seguridad";
    document.getElementById("lblSMSSentTo").textContent = "SMS enviado a:";
    document.getElementById("resend").textContent = "VOLVER A ENVIAR EL CÓDIGO";
    document.getElementById("submitBtn").textContent = "VERIFICAR";
}

