// check for blank fields in PingOne
// If country code is left (+1) then phone will return true, even though you did not actually enter a phone number
module.exports = a = async ({params}) => {

	let jsonObject = params.matchedUserObject;
	// Initialize variables to hold the boolean values
    let hasDob = jsonObject.hasOwnProperty('dob');
    let hasGivenName = jsonObject.hasOwnProperty('name') && jsonObject.name.hasOwnProperty('given');
    let hasMiddleName = jsonObject.hasOwnProperty('name') && jsonObject.name.hasOwnProperty('middle');
    let hasFamilyName = jsonObject.hasOwnProperty('name') && jsonObject.name.hasOwnProperty('family');
    let hasPrimaryPhone = jsonObject.hasOwnProperty('primaryPhone');

     // Check if all properties are true
    let allTrue = hasDob && hasGivenName && hasMiddleName && hasFamilyName && hasPrimaryPhone;

    // Explicitly set the return statement
    return {
        hasDob: hasDob,
        hasGivenName: hasGivenName,
        hasMiddleName: hasMiddleName,
        hasFamilyName: hasFamilyName,
        hasPrimaryPhone: hasPrimaryPhone,
        allTrue: allTrue
    };
}

