const focusOnFirstInputElement = () => {
    document.getElementById("username").focus();
};
console.log('starting');
let forcePasswordless = {{global.company.variables.gv-forcePasswordless}};
console.log(forcePasswordless);
if (forcePasswordless !== true) {
    const pswdPolicy = {{global.company.variables.gv-p1PasswordPolicy}};
    let confirmPasswordFocused = false;

    var minCharacters = pswdPolicy.minCharacters;

    // Update rules based on the JSON object
    if (minCharacters.hasOwnProperty("abcdefghijklmnopqrstuvwxyz")) {
        document.getElementById("letter").style.display = "block";
    }
    if (minCharacters.hasOwnProperty("ABCDEFGHIJKLMNOPQRSTUVWXYZ")) {
        document.getElementById("capital").style.display = "block";
    }
    if (minCharacters.hasOwnProperty("0123456789")) {
        document.getElementById("number").style.display = "block";
    }
    if (minCharacters.hasOwnProperty("~!@#$%^&*()-_=+[]{}|;:,.<>/?")) {
        document.getElementById("space").style.display = "block";
    }
    if (pswdPolicy.notSimilarToCurrent) {
        document.getElementById("similar").style.display = "block";
    }
    if (pswdPolicy.minUniqueCharacters) {
        document.getElementById("unique").style.display = "block";
    }
    if (pswdPolicy.maxRepeatedCharacters) {
        document.getElementById("repeating").style.display = "block";
    }
    if (pswdPolicy.length) {
        document.getElementById("length").style.display = "block";
    }

    $('#newPassword').keyup(function () {
        var pswd = $(this).val();
        var allRequirementsMet = true;

        if (pswd.length > 0) {
            $('#pswd_info').show();
        } else {
            $('#pswd_info').hide();
        }

        if (minCharacters.hasOwnProperty("abcdefghijklmnopqrstuvwxyz")) {
            var letterReqMet = /[a-z]/.test(pswd);
            allRequirementsMet = allRequirementsMet && letterReqMet;
            updateRequirement('#letter', letterReqMet);
        }
        if (minCharacters.hasOwnProperty("ABCDEFGHIJKLMNOPQRSTUVWXYZ")) {
            var capitalReqMet = /[A-Z]/.test(pswd);
            allRequirementsMet = allRequirementsMet && capitalReqMet;
            updateRequirement('#capital', capitalReqMet);
        }
        if (minCharacters.hasOwnProperty("0123456789")) {
            var numberReqMet = /\d/.test(pswd);
            allRequirementsMet = allRequirementsMet && numberReqMet;
            updateRequirement('#number', numberReqMet);
        }
        if (minCharacters.hasOwnProperty("~!@#$%^&*()-_=+[]{}|;:,.<>/?")) {
            var spaceReqMet = /[^a-zA-Z0-9\-\/]/.test(pswd);
            allRequirementsMet = allRequirementsMet && spaceReqMet;
            updateRequirement('#space', spaceReqMet);
        }
        if (pswdPolicy.minUniqueCharacters) {
            var uniqueReqMet = new Set(pswd).size >= pswdPolicy.minUniqueCharacters;
            allRequirementsMet = allRequirementsMet && uniqueReqMet;
            updateRequirement('#unique', uniqueReqMet);
        }
        if (pswdPolicy.maxRepeatedCharacters) {
            // Check if the password is not empty and does not contain repeating characters
            var repeatingReqMet = pswd.length > 0 && !/(.)\1{2,}/.test(pswd);
            allRequirementsMet = allRequirementsMet && repeatingReqMet;
            updateRequirement('#repeating', repeatingReqMet);
        }
        if (pswdPolicy.length) {
            var lengthReqMet = pswd.length >= pswdPolicy.length.min;
            allRequirementsMet = allRequirementsMet && lengthReqMet;
            updateRequirement('#length', lengthReqMet);
        }




        // Update border color based on overall status
        if (allRequirementsMet) {
            $('#pswd_info').css('border', '1px solid #d6ffe7');
        } else {
            $('#pswd_info').css('border', ''); // Reset to default or previous style
        }
        if (confirmPasswordFocused) {
            validatePasswords();
        }

        enableSubmitButton(); // Check all conditions and enable/disable the submit button

    }).focus(function () {
        var pswd = $(this).val();
        console.log(pswd.length);
        if (pswd.length > 0) {
            $('#pswd_info').show();
        } else {
            $('#pswd_info').hide();
        }
    }).blur(function () {
        $('#pswd_info').hide();
    });

    function updateRequirement(selector, isValid) {
        var element = $(selector);
        var originalText = element.text().replace(/<[^>]*>/g, ''); // Remove HTML to keep the original text

        if (isValid) {
            element.addClass('valid').html('<i class="mdi mdi-check-circle"></i> ' + originalText);
        } else {
            element.removeClass('valid').html(originalText);
        }

        return isValid;
    }

    const confirmPasswordInput = document.getElementById("confirmPassword");
    const matchFeedback = document.getElementById("matchFeedback");

    function validatePasswords() {
        const newPassword = document.getElementById("newPassword").value;
        const confirmPassword = confirmPasswordInput.value;

        if (newPassword === confirmPassword) {
            matchFeedback.style.display = "none";
        } else {
            matchFeedback.style.display = "block";
        }
    }
    // Add a focus event listener to "confirmPassword" field
    $('#confirmPassword').on('focus', function () {
        confirmPasswordFocused = true;
    });

    // Add a blur event listener to "confirmPassword" field
    $('#confirmPassword').on('blur', function () {
        confirmPasswordFocused = false;
    });

    // Add an input event listener to "confirmPassword" field
    $('#confirmPassword').on('input', function () {
        // Validate passwords when "confirmPassword" input changes
        if (confirmPasswordFocused) {
            validatePasswords();
            enableSubmitButton(); // Check all conditions and enable/disable the submit button

        }
    });

    function enableSubmitButton() {
        var pswd = $('#newPassword').val();
        var allRequirementsMet = true;

        if (minCharacters.hasOwnProperty("abcdefghijklmnopqrstuvwxyz")) {
            allRequirementsMet = allRequirementsMet && updateRequirement('#letter', /[a-z]/.test(pswd));
        }
        if (minCharacters.hasOwnProperty("ABCDEFGHIJKLMNOPQRSTUVWXYZ")) {
            allRequirementsMet = allRequirementsMet && updateRequirement('#capital', /[A-Z]/.test(pswd));
        }
        if (minCharacters.hasOwnProperty("0123456789")) {
            allRequirementsMet = allRequirementsMet && updateRequirement('#number', /\d/.test(pswd));
        }
        if (minCharacters.hasOwnProperty("~!@#$%^&*()-_=+[]{}|;:,.<>/?")) {
            allRequirementsMet = allRequirementsMet && updateRequirement('#space', /[^a-zA-Z0-9\-\/]/.test(pswd));
        }
        if (pswdPolicy.minUniqueCharacters) {
            allRequirementsMet = allRequirementsMet && updateRequirement('#unique', new Set(pswd).size >= 5);
        }
        if (pswdPolicy.maxRepeatedCharacters) {
            allRequirementsMet = allRequirementsMet && updateRequirement('#repeating', !/(.)\1{2,}/.test(pswd));
        }
        if (pswdPolicy.length) {
            allRequirementsMet = allRequirementsMet && updateRequirement('#length', pswd.length >= pswdPolicy.length.min);
        }

        var newPassword = $('#newPassword').val();
        var confirmPassword = $('#confirmPassword').val();
        var passwordsMatch = newPassword === confirmPassword;

        if (allRequirementsMet && passwordsMatch) {
            $('#submitBtn').prop('disabled', false);
        } else {
            $('#submitBtn').prop('disabled', true);
        }

        // Enable or disable the submit button based on both conditions
        if (allRequirementsMet && passwordsMatch) {
            $('#submitBtn').prop('disabled', false); // Enable the submit button
        } else {
            $('#submitBtn').prop('disabled', true); // Disable the submit button
        }

        if (confirmPasswordFocused) {
            validatePasswords();
        }
    }

    enableSubmitButton();

    function makePasswordToggle(id) {
        var container = document.getElementById(id);
        var password = container.getElementsByTagName("input")[0];
        var toggler = document.createElement("button");
        toggler.setAttribute("type", "button");
        toggler.setAttribute("aria-label", "Show/Hide Password");
        toggler.className = "btn mdi mdi-eye-off-outline position-absolute end-0 top-50 translate-middle-y";
        container.appendChild(toggler);

        function showHidePassword() {
            if (password.type == "password") {
                password.setAttribute("type", "text");
                toggler.classList.add("mdi-eye-outline");
                toggler.classList.remove("mdi-eye-off-outline");
            } else {
                toggler.classList.add("mdi-eye-off-outline");
                toggler.classList.remove("mdi-eye-outline");
                password.setAttribute("type", "password");
            }
            password.focus();
        };

        toggler.addEventListener("click", showHidePassword);
    }

    function start() {
        makePasswordToggle("passwordContainer");
        makePasswordToggle("confirmPasswordContainer");
        focusOnFirstInputElement();
    }



    if (document.readyState === "loading") {
        // Loading hasn't finished yet
        document.addEventListener("DOMContentLoaded", start);
    } else {
        // `DOMContentLoaded` has already fired
        start();
    }
}

