settings.css:
  :root {
    /********************************************************
    * COMPANY LOGO
    *********************************************************/
    --company-logo-img-url: url("https://assets.pingone.com/ux/ui-library/5.0.2/images/logo-pingidentity.png");
    --company-logo-max-height: 65px;

    /********************************************************
    * PAGE BACKGROUND 
    *********************************************************/
    /* --background-image-url: url(""); */

    /********************************************************
    * CARD BODY
    *********************************************************/
    --card-background-color: rgb(255, 255, 255);

    /********************************************************
    * BODY
    *********************************************************/
    --body-font-family: BlinkMacSystemFont;
    --body-font-size: 0.9rem;

    /********************************************************
    * TEXT FONT COLORS
    *********************************************************/

    /*  PRIMARY FONT COLOR (RGB) */
    --text-color-primary: 61, 69, 77;

    /* HEADING FONT COLR (RBG) */
    --heading-text-color: 61, 69, 77;

    /*  MUTED TEXT ALPHA  */
    --text-color-muted-apha: 0.75;

    /*  ERROR FONT COLOR  */
    --text-color-error: rgb(163, 19, 0);

    /********************************************************
    * BUTTON ATTRIBUTES
    *********************************************************/

    /*  BUTTON FONT COLOR  */
    --button-primary-text-color: rgb(255, 255, 255);

    /*  BUTTON BACKGROUND COLOR (RGB)  */
    --button-primary-background-color: 39, 123, 165;

    /* LINK BUTTON FONT COLOR (RGB)  */
    --link-button-primary-color: 39, 123, 165;

    /*  BUTTON HOVER ALPHA  */
    --button-hover-alpha: 0.70;

    /*  BUTTON ACTIVE ALPHA  */
    --button-active-alpha: 0.80;

    /*  BUTTON DISABLED ALPHA  */
    --button-disabled-alpha: 0.65;

    /********************************************************
    * ACTIVITY INDICATOR & BUTTON SPINNER COLOR
    *********************************************************/

    --activity-indicator-color: rgb(68, 98, 237);

    --polling-indicator-color: rgb(68, 98, 237);

    /********************************************************
    * APPLYING TEXT COLOR - DO NOT CHANGE
    *********************************************************/
    /* H1,H2, H3, Body text color */
    --bs-body-color: rgb(var(--text-color-main)) !important;
    --bs-danger-rgb: var(--text-color-error);

    /********************************************************
    * STEPPER CONTROLS
    ********************************************************/
    --active-fill-color: var(--button-primary-background-color);
    --line-shadow-color: var(--button-primary-background-color);
    --incomplete-text-color: rgb(0, 0, 0);    
  }



/********************************************************
* CLASSES BELOW ARE DERIVED FROM VALUES ABOVE
* DO NOT CHANGE UNLESS YOU HAVE CSS 'SKILLZ' :)
*********************************************************/

/*  COMPANY LOGO  */
.companyLogo {
  content: var(--company-logo-img-url) !important;
  height: var(--company-logo-max-height) !important;
  width: auto !important;
}

/*  BACKGROUND IMAGE  */
div.bg-light {
  background-image:       var(--background-image-url);
  background-size:        cover;
  background-repeat:      no-repeat;
}

/*  HEADING TEXT  */
h1, h2, h3, h4 {
  color: rgb(var(--heading-text-color)) !important;
}

/*  BODY FONT & COLOR  */
.card-body {
  /*  font family */
  font-family: var(--body-font-family) !important;
  /*  font size */
  font-size: var(--body-font-size) !important;
  /*  font color */
  color: rgb(var(--text-color-primary)) !important;
  /*  background color */
  background-color: var(--card-background-color) !important;
}

/*  MUTED PARAGRAPH  */
p.text-muted {
  color: rgba(var(--text-color-primary), var(--text-color-muted-apha)) !important;
}

/*  ERROR MESSAGE PARAGRAPH  */
.card .text-danger {
  color: var(--text-color-error) !important;
}

/*  FORM CONTROL FONT COLOR  */
.form-control,
.form-control:focus {
  color: var(--text-color-primary) !important;
}

/*  FORM CONTROL PLACEHOLDER FONT COLOR  */
.form-floating input+label {
  color: rgba(var(--text-color-primary), var(--text-color-muted-apha)) !important;
}

/*  FORM CONTROL HIGHLIGHT  */
.form-control:focus {
  border-color: rgba(var(--button-primary-background-color), .5) !important;
  box-shadow: 0 0 0 0.25rem rgba(var(--button-primary-background-color), 0.25) !important;
}

/*  PRIMARY BUTTON  */
.btn-primary {
  /*  font color  */
  --bs-btn-color: var(--button-primary-text-color) !important;
  /*  font color when hovering  */
  --bs-btn-hover-color: var(--button-primary-text-color) !important;
  /*  border color  */
  --bs-btn-border-color: var(--button-primary-background-color) !important;
  /*  background color  */
  --bs-btn-bg: rgb(var(--button-primary-background-color)) !important;
  /*  background color when hovering  */
  --bs-btn-hover-bg: rgba(var(--button-primary-background-color), var(--button-hover-alpha)) !important;
  /*  background color when active  */
  --bs-btn-active-bg: rgba(var(--button-primary-background-color), var(--button-active-alpha)) !important;
  /*  background color when disabled  */
  --bs-btn-disabled-bg: rgba(var(--button-primary-background-color), var(--button-disabled-alpha)) !important;
  background-color: rgb(var(--button-primary-background-color)) !important;
  border-color: rgb(var(--button-primary-background-color)) !important;
  color: var(--button-primary-text-color) !important;
}


.btn-primary:hover {
  background-color: rgba(var(--button-primary-background-color), var(--button-hover-alpha)) !important;
  border-color: rgba(var(--button-primary-background-color), var(--button-hover-alpha)) !important;

  color: var(--button-primary-text-color) !important;
}

/*  LINK BUTTON  */
.btn-link {
  /*  button font color  */
  --bs-btn-color: rgb(var(--link-button-primary-color)) !important;
  /*  button font color when active  */
  --bs-btn-active-color: rgba(var(--link-button-primary-color), var(--button-active-alpha)) !important;
  /*  button font color when hovering  */
  --bs-btn-hover-color: rgba(var(--link-button-primary-color), var(--button-hover-alpha)) !important;
  /*  button font color when disabled  */
  --bs-btn-disabled-color: rgba(var(--link-button-primary-color), var(--button-disabled-alpha)) !important;
  color: rgb(var(--link-button-primary-color)) !important;
}

/*  POLLING INDICATORS  */
.css-11k6vsm,
.css-17zi2ag,
.css-139roxj {
  /* width: 20px; */
  /* height: 20px; */
  /* animation-duration: 0.75s; */
  /*  circle background color  */
  background-color: var(--polling-indicator-color) !important;
}

/*  BUTTON ACTIVITY INDICATOR  */
.css-sw2ho0 {
  /*  spinning circle color  */
  --primary-color: var(--activity-indicator-color) !important;
}



/********************************************************
*  STEPPER
*********************************************************/
.stepper-wrapper {
  font-family: var(--body-font-family);
  font-size: 0.75rem;
  margin-top: 20px;
  margin-bottom: 30px;
  display: flex;
  justify-content: space-between;
  }

  .step-name {
  color: rgb(var(--text-color-primary));
  }

  .stepper-item {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  flex: 1;
  }

  .stepper-item::before {
  position: absolute;
  content: "";
  border-bottom: 2px solid rgb(var(--line-shadow-color));
  width: 100%;
  top: 15px;
  left: -50%;
  z-index: 2;
  }

  .stepper-item::after {
  position: absolute;
  content: "";
  border-bottom: 2px solid rgb(var(--line-shadow-color));
  width: 100%;
  top: 15px;
  left: 50%;
  z-index: 2;
  }

  .stepper-item .step-counter {
  position: relative;
  z-index: 5;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  border: 1px solid rgb(var(--line-shadow-color));
  background-color: white;
  margin-bottom: 6px;
  font-weight: bold;
  color: rgb(var(--incomplete-text-color));
  /*color: var(--incomplete-text-color);*/
  }

  .stepper-item.active {
  font-weight: bold;
  }

  .stepper-item.active .step-counter {
  background-color: rgb(var(--active-fill-color));
  border: 1px solid rgb(var(--button-primary-background-color));
  color: var(--button-primary-text-color);
  }

  .stepper-item.finished .step-counter {
  background-color: var(--activity-indicator-color);
  border: 1px solid var(--activity-indicator-color);
  font-size: 0;
  }

  .stepper-item.completed .step-counter {
  background-color: rgb(var(--button-primary-background-color));
  border: 1px solid rgb(var(--button-primary-background-color));
  font-size: 0; /* Hide the step number */
  line-height: 0;
  overflow: hidden;
  }

  .stepper-item.completed .step-counter::before,
  .stepper-item.finished .step-counter::before {
  content: "\2713";
  color: var(--button-primary-text-color);
  display: inline-block;
  line-height: normal;
  font-size: 10pt;
  }

  .stepper-item.completed::after {
  position: absolute;
  content: "";
  border-bottom: 1px solid rgb(var(--button-primary-background-color));
  width: 100%;
  top: 15px;
  left: 50%;
  z-index: 3;
  }

  .stepper-item:first-child::before {
  content: none;
  }
  .stepper-item:last-child::after {
  content: none;
  }


/********************************************************
* MOBILE FORMATTING
*********************************************************/
@media only screen and (max-width: 600px) {

  /* Modify background and page positioning */
  body {
    background-color: var(--card-background-color) !important;
    padding: 15px !important;
  }

  /* Increase font sizes */
  h1 {
    font-size: 1.6rem !important;
  }

  .btn {
    font-size: 18px;
  }

  .card-body {
    font-size: 16px;
  }

  /* Add border radius on form controls to align with current mobile ux */
  .form-control {
    border-radius: 8px !important;
  }

  /* Increase the margins from default */
  .mb-3 {
    margin-bottom: 15px !important;
  }

  .mb-4 {
    margin-bottom: 20px !important;
  }

  .mb-5 {
    margin-bottom: 25px !important;
  }

  /* Remove background image and border */
  div.bg-light.d-flex {
    /*background-color: var(--bs-card-color) !important;*/
    background-color: var(--card-background-color) !important;
    background-image: none;
    border: none;
    bottom: auto !important;
    margin-top: 30px;
    width: 100%;
  }

  /* Remove default page centering */
  .bg-light>div {
    left: 0 !important;
    position: unset !important;
  }

  /* Remove border and shadow */
  div.card.shadow {
    border: none;
    box-shadow: none !important;
  }
}

/*  CUSTOM ROUNDED CORNERS CARD BODY  */
div.shadow {
  box-shadow: 0 1px 4px 2px rgb(121 128 135 / 52%) !important;
  border-radius: 10px !important;
}

div.card-body {
  border-radius: 10px !important;
}

