/**
 * Generate HTML for form progress control.
 * 
 * Note: This HTML relies on CSS classes that are not present within Ping's standard CSS (yet)
 *       Add the following CSS document to your flow
 *       https://davinci-progress-stepper.glitch.me/style.css
 *       
 *       or to inverse the fill colors
 *       https://davinci-progress-stepper.glitch.me/style-inverse.css
 * 
 *       The CSS attributes are compliant with themeing via flow runner and the chrome extension
 * 
 * @param {Array} stepperItems - Array of strings for stepper items.
 * @returns {object} - HTML string for the each page's stepper control.
 */
module.exports = async ({ params }) => {
  const stepperItems = params.stepperItems;
  const showLabels = params.showLabels;

  if (!Array.isArray(stepperItems)) {
    throw new Error('Invalid stepperItems parameter. Expected an array.');
  }

  let stepperPages = {};
  for (const page of stepperItems) {
    let activeIndex = stepperItems.findIndex(item => item.toLowerCase() === page.toLowerCase());
    stepperPages[page.toLowerCase()] = buildStepperPage(activeIndex, stepperItems, showLabels);
  }
  return { "stepperPages": stepperPages };
}

const buildStepperPage = (activeIndex, stepperItems, showLabels) => {
  let stepperHTML = `<div class="stepper-wrapper">`;
  let labelClass = showLabels? "" : "d-none"
  stepperItems.forEach((item, index) => {
    let itemState = "";
    if (activeIndex === index && index === stepperItems.length - 1) {
      itemState = "finished"
    } else if (activeIndex === index) {
      itemState = "active";
    } else if (activeIndex > index) {
      itemState = "completed";
    }

    stepperHTML += `
        <div class="stepper-item ${itemState}" title="${item}">
          <div class="step-counter">${index + 1}</div>
          <div class="step-name ${labelClass}">${item}</div>
        </div>
      `;
  });

  stepperHTML += `</div>`;
  return stepperHTML;
}

