// Supported language: Javascript 
module.exports = a = async ({params}) => {

	const flattenedCredentialDataList = {}

	params.credentialVerificationData.credentialsDataList.forEach((credential, index) => {
		const credentialName = credential.type;
		flattenedCredentialDataList[credentialName] = {
			  id: credential.id,			
			  type: credential.type,
			  verificationStatus: credential.verificationStatus,
			  issuerName: credential.issuerName,
			  issuerId: credential.issuerId,
			  data: {}
	    };

		credential.data.forEach(dataItem => {
			if (dataItem.key == 'CardImage') {
				// removes image from the data object into its own property for improved downstream handling
				flattenedCredentialDataList[credentialName].cardImage = dataItem.value;
			} else {
				// data object replaces multiple unnamed key:value objects by flattening to a single
				// object where the keys are the claim names, and assigned its associated value
				flattenedCredentialDataList[credentialName].data[dataItem.key] = dataItem.value;
			}
		});
	});

	return {'flattenedCredentialDataList': flattenedCredentialDataList}
}

