module.exports = a = async ({params}) => {

	pairingNotificationResults = params.pairingNotificationResults

	// Determine if a pairing notification was sent via "sms"
	const smsNotification = pairingNotificationResults.find((result) => result.method === "SMS");

	if ( smsNotification?.sent === true ) {
		return {'notificationSent': true}
	}

    // Didn't find successfully sent wallet pairing notification
    return false

}

