const start = () => {
    try {
        // Initialize form validation
        DaVinciFormUtils.addDaVinciFormValidation({
            formId: 'otpForm', // required
            formType: 'STANDARD', // required (UPDATE_PASSWORD or STANDARD)
            delayValidationUntilSubmit: true,
            invalidFieldBorderColor: "red",
            disableSubmitButtonUntilValid: true
        });

        // Validate OTP Input with custom validation logic
        masterFlowFormUtils.validateOTPInput({
            inputId: 'verificationCode',
            feedbackId: 'OTPFeedback',
            submitId: 'submit',
            highlightInput: true,
            autoSubmit: true,
            customValidation: checkInputValid // Add custom validation function
        });

    } catch (error) {
        console.error("Error initializing form validation:", error);
    }
}

// Example function to check if input is valid
function checkInputValid(input) {
    try {
        // Validation logic: Allow any combination of 8 letters (a-z, A-Z) or numbers (0-9)
        return input.length === 8 && /^[a-zA-Z0-9]+$/.test(input);
    } catch (error) {
        console.error("Error validating input:", error);
        return false; // Assume invalid input if there's an error
    }
}

// Attach the validation on DOMContentLoaded
if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

