function start() {
  setPasswordForm
  DaVinciFormUtils.addDaVinciFormValidation({
    formId: 'setPasswordForm', // required
    formType: 'UPDATE_PASSWORD', // required (UPDATE_PASSWORD or STANDARD)
    passwordPolicy: '{{global.variables.passwordPolicy}}', // required for UPDATE_PASSWORD forms
    passwordFieldContainerId: 'newPasswordContainer', // required for UPDATE_PASSWORD forms
    confirmPasswordFieldContainerId: 'verifyPasswordContainer', // required for UPDATE_PASSWORD forms
    invalidFieldBorderColor: "red",
    disableSubmitButtonUntilValid: true
  });
}

if (document.readyState === "loading") {
  // Loading hasn't finished yet
  document.addEventListener("DOMContentLoaded", start);
} else {
  // `DOMContentLoaded` has already fired
  start();
}

