const maxLength = 6;
function checkInputValid(val) {
    if (maxLength === "44") {
        if (/[a-z]{44}/.test(val)) {
            return true;
        }
    } else if (maxLength === "8") {
        if (/^[0-9]{6}$|^[0-9]{8}$/.test(val)) {
            return true;
        }
    } else {
        if (/^[0-9]{6}$/.test(val)) {
            return true;
        }
    }
    return false;
}

document.getElementById('passcode').addEventListener('focus', function() {
    $('#passcode').css('border', '1px solid red');
});

document.getElementById('passcode').addEventListener('input', function() {
    var inputVal = this.value;

    // Check if the input is valid
    if (checkInputValid(inputVal)) {
        // If valid, delay the submission to allow the last digit to appear
        $('OTPFeedback').css('display', 'none');
        $('#passcode').css('border', '1px solid #8b9197');
        setTimeout(function() {
            document.getElementById('submit').click();
        }, 100); // Delay in milliseconds
    } else {
        $('OTPFeedback').css('display', 'block');
        $('#passcode').css('border', '1px solid red');
    }
});

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.onreadystatechange = () => {
        if (document.readyState === "complete") {
