    const dropdownButton = document.getElementById('dropdownMenuButton');
    const hideButtonElement = document.getElementById(hideButtonElementId);
    console.log(hideButtonElement);

    if (dropdownButton && hideButtonElement) {
        dropdownButton.addEventListener('click', function () {
            hideButtonElement.style.setProperty('display', 'none', 'important');
            console.log('hidingButton' + hideButtonElementId);
        });
    }
}
const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    }
}

const start = () => {
    setFieldFocus("passcode")
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
