module.exports = a = async ({params}) => {
let devices = params.devices; 

// Find all devices with type "MAGIC"
const magicLinkDevices = devices.filter(device => device.type === 'MAGIC');

// Declare selected Device Id
const selectedDeviceId = params.buttonValue; 

// Find the specific magic link device by its ID
const specificMagicLinkDevice = magicLinkDevices.find(device => device.id === selectedDeviceId);

// Check if a specific magic link device was found and return its email
if (specificMagicLinkDevice) {
    return { 'email': specificMagicLinkDevice.email };
} else {
    return false;
}

}

