module.exports = a = async ({params}) => {
	let devices = params.devices;
	let selectedDeviceId = params.selectedDeviceId;

	   for (const device of devices) {
        if (device.id === selectedDeviceId) {
            return; // ID is found in the devices array
        }
    }
    return false;

}

