:root {
    --failure-animation-dimension: 120px;
    --failure-animation-start-color: #e63946; 
    --failure-animation-midway-color: #d90429; 
    --failure-animation-finish-color: #ba000d; 
}

.xmark {
    width: var(--failure-animation-dimension);
    height: var(--failure-animation-dimension);
    display: block;
    stroke-width: 5;
    stroke: #ffffff;
    stroke-miterlimit: 10;
    border-radius: 50%;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    animation: fill 0.4s ease-in-out 0.4s forwards, scale 0.3s ease-in-out 0.9s both;
}

.xmarkCircle {
    stroke-dasharray: 166;
    stroke-dashoffset: 166;
    stroke-width: 5;
    stroke: var(--failure-animation-finish-color);
    fill: none;
    animation: stroke 0.6s cubic-bezier(0.65, 0, 0.45, 1) forwards;
}

.xmarkX {
    stroke-dasharray: 56;
    stroke-dashoffset: 56;
    stroke-width: 5;
    stroke: var(--failure-animation-start-color);
    animation: strokeX 0.3s cubic-bezier(0.65, 0, 0.45, 1) 0.8s forwards, colorChangeX 0.6s ease-in-out 0.8s forwards;
}

@keyframes stroke {
    to {
        stroke-dashoffset: 0;
    }
}

@keyframes strokeX {
    to {
        stroke-dashoffset: 0;
    }
}

@keyframes colorChangeX {
    0% {
        stroke: var(--failure-animation-start-color);
    }
    50% {
        stroke: var(--failure-animation-midway-color);
    }
    100% {
        stroke: var(--failure-animation-finish-color);
    }
}

@keyframes scale {
  0%,
  100% {
    transform: none;
  }
  50% {
    transform: scale3d(1.1, 1.1, 1);
  }
  70% {
    transform: scale3d(0.98, 0.98, 1);
  }
  85% {
    transform: scale3d(1.02, 1.02, 1);
  }
}

/* Ensure scale and fill animations are correctly applied */

