// DOM elements
const phone                 = document.getElementById("phone"); // Phone input field
const phoneCodeDropdown     = document.getElementById("phone-input-code-dropdown"); // Phone code dropdown
const countryCodeInput      = document.getElementById("countryCodeInput"); // Country code input field
const phoneCodeList         = document.getElementById("code-dropdown-list"); // Dropdown list for phone codes
const phoneCode             = document.getElementById("phone-input-code"); // Phone input code container
const closeListBtn          = document.getElementById("close-list"); // Button to close the dropdown list
const listContainer         = document.getElementById("list-container"); // Container for the list
const search                = document.getElementById("search-input"); // Search input field
const submitButton          = document.getElementById('submit'); // Submit button

// Initializations
countryCodeInput.value = "+1"; // Set default country code to +1

// Add event listeners to dropdown options
const items = document.getElementsByClassName("dropdown__option");
for (let i = 0; i < items.length; i++) {
    items[i].addEventListener("click", selectClick);
}

// Functions
// Function to close the dropdown list
function closeList() {
    phoneCodeDropdown.classList.remove("dropdown--open");
    phoneCode.classList.remove("phone-input__code--open");
    phoneCodeList.classList.add("none");
    setFieldFocus("phone");
}

// Function to handle selection of a country code
function selectClick(event) {
    const codeIndex = event.target.innerHTML.indexOf("+");
    countryCodeInput.value = event.target.innerHTML.substring(codeIndex);
    closeList();
}

// Function to open the dropdown list
function openList() {
    phoneCodeDropdown.classList.add("dropdown--open");
    phoneCode.classList.add("phone-input__code--open");
    phoneCodeList.classList.remove("none");
    setFieldFocus("search-input");
}

// Function to search and filter the dropdown list
function searchList() {
    const searchValue = search.value.toLowerCase();
    for (let i = 0; i < items.length; i++) {
        const itemText = items[i].innerHTML.toLowerCase();
        items[i].classList.toggle("none", !(itemText.includes(searchValue) || searchValue.length < 1));
    }
}

// Event listener for phone input changes
phone.addEventListener('input', function() {
    var phoneNumber = $('#phone').val();
    checkInputValid(phoneNumber);
});

// Event listener for phone input focus
phone.addEventListener('focus', function() {
    var phoneNumber = $('#phone').val();
    checkInputValid(phoneNumber);
});

// Function to check if the input value is valid
function checkInputValid(val) {
    var inputValid = val.trim().length > 4;
    if (inputValid) {
        $('#phone').css('border', '1px solid #8b9197');
        submitButton.classList.remove('disabled');
    } else {
        $('#phone').css('border', '1px solid red');
        submitButton.classList.add('disabled');
    }
    return val.trim().length > 4;
}

// Function to set focus to a specific field
const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    }
}

// DaVinci form validation setup
const start = () => {
    DaVinciFormUtils.addDaVinciFormValidation({
        formId: 'profileForm', // required
        formType: 'STANDARD', // required (UPDATE_PASSWORD or STANDARD)
        delayValidationUntilSubmit: false,
        invalidFieldBorderColor: "red",
        disableSubmitButtonUntilValid: false
    });
}

// Ensure the DOM is fully loaded before running the start function
if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

// Event listeners for opening and closing the list and searching within the list
if (window.addEventListener) {
    // Normal browsers
    countryCodeInput.addEventListener("click", openList);
    closeListBtn.addEventListener("click", closeList);
    search.addEventListener("input", searchList);
} else if (window.attachEvent) {
    // IE
    countryCodeInput.attachEvent("click", openList);
    closeListBtn.attachEvent("click", closeList);
    search.attachEvent("input", searchList);
}

