        position: absolute;
        right: 15px;
        top: 16px;
        height: 16px;
        width: 16px;
        pointer-events: none;
        margin-right: 25px
    }

    .dropdown--open:after {
        -webkit-transform: translateY(-50%) rotate(180deg);
        transform: translateY(-50%) rotate(180deg)
    }

    .dropdown__input,
    .dropdown__select {
        cursor: pointer;
        padding-right: 40px
    }

    .dropdown__input--error,
    .dropdown__select--error {
        border-color: #a31300
    }

    .dropdown__input--success,
    .dropdown__select--success {
        border-color: #4aba78
    }

    .dropdown__input--error,
    .dropdown__input--success,
    .dropdown__select--error,
