        content: "\25BC";
        font-size: 13px;
        line-height: 13px;
        position: absolute;
        right: 15px;
        top: 16px;
        height: 16px;
        width: 16px;
        pointer-events: none;
        margin-right: 25px
    }

    .dropdown--open:after {
        -webkit-transform: translateY(-50%) rotate(180deg);
        transform: translateY(-50%) rotate(180deg)
    }

    .dropdown__input,
    .dropdown__select {
        cursor: pointer;
        padding-right: 40px
    }

    .dropdown__input--error,
    .dropdown__select--error {
        border-color: #a31300
    }

    .dropdown__input--success,
    .dropdown__select--success {
        border-color: #4aba78
    }

    .dropdown__input--error,
    .dropdown__input--success,
    .dropdown__select--error,
    .dropdown__select--success {
        background-color: #fff;
        padding-right: 65px
    }

    .dropdown__select {
        position: relative;
        z-index: 1;
        width: 100%;
        background-color: transparent;
        background-image: none;
        -webkit-appearance: none;
        -moz-appearance: none;
        appearance: none
    }

    .dropdown__select.placeholder-shown {
        color: hsla(0, 0%, 100%, 0)
    }

    .dropdown__select.dropdown__select--placeholder {
        font-weight: 200
    }

    .dropdown__list {
        -webkit-box-shadow: 0 1px 4px 1px rgba(121, 128, 135, .35);
        box-shadow: 0 1px 4px 1px rgba(121, 128, 135, .35);
        position: absolute;
        top: 100%;
        left: 0;
        right: 0;
        background: #fff;
        margin: -1px 0 0;
        padding: 0;
        list-style: none;
        z-index: 1
    }

    .dropdown--cardwidth .dropdown__list {
        width: 320px
    }

    .dropdown__option {
        cursor: pointer;
        padding: 5px 10px 5px 20px;
        position: relative
    }

    .dropdown__option:hover {
        color: #fff;
        background: #2996cc
    }

    .dropdown__option--selected {
        font-weight: 600
    }

    .dropdown__option--selected:before {
        content: "\2022";
        position: absolute;
        left: 10px;
        top: 50%;
        -webkit-transform: translateY(-50%);
        transform: translateY(-50%);
        vertical-align: middle
    }

    .dropdown__option--search {
        position: relative
    }

    .dropdown--search .dropdown__list {
        height: 250px;
        top: 0
    }

    .dropdown--search .dropdown__list .dropdown__option--container {
        position: absolute;
        height: 200px;
        overflow-y: scroll;
        width: 100%
    }

    .dropdown--search.dropdown--open:after {
        content: none
    }

    .dropdown--search.dropdown--open .dropdown__close {
        display: block;
        position: absolute;
        top: 0;
        bottom: 0;
        right: 0;
        width: 30px;
        cursor: pointer;
        background: none;
        border: none
    }

    .dropdown--search.dropdown--open .dropdown__close:after {
        font-family: iconfont;
        /* content: "\2304"; */
        content: "\25BC";
        font-size: 13px;
        line-height: 13px;
        position: absolute;
        right: 15px;
        top: 16px;
        height: 16px;
        width: 16px;
        pointer-events: none;
        -webkit-transform: translateY(-50%) rotate(180deg);
        transform: translateY(-50%) rotate(180deg)
    }

    .dropdown--search .dropdown__close {
        display: none
    }

    .dropdown__icon {
        font-family: iconfont;
        font-size: 16px;
        line-height: 16px;
        text-transform: none;
        position: relative;
        width: 100%
    }

    .dropdown__icon:before {
        position: absolute;
        right: 15px;
        top: 14.5px
    }

    .dropdown__icon--error:before {
        content: "\E914";
        color: #a31300
    }

    .dropdown__icon--success:before {
        content: "\E92E";
        color: #4aba78
    }

    .phone-input {
        position: relative;
        height: 48px;
        width: 100%;
        max-width: 350px
    }

    .phone-input__code {
        width: calc(25% - 5px);
        margin-right: 5px;
        -webkit-transition: all .2s;
        transition: all .2s;
        position: absolute;
        top: 0;
        left: 0;
        z-index: 10
    }

    .phone-input__code--open {
        width: 100%
    }

    .phone-input__number {
        position: absolute;
        top: 0;
        right: 0;
        width: 75%
    }

    .phone-input__longDialCode .phone-input__code {
        width: calc(30% - 5px)
    }

    .phone-input__longDialCode .phone-input__number {
        width: 70%
    }

    .none {
        display: none;
    }

    .dropdown--standard:after {
        right: 10px;
    }

    .dropdown__input {
        padding-right: 26px;
    }

    .mdi-chevron-down::before {
        content: "\ea01"
    }

    /* hide input tyme number arrows */
    /* Chrome, Safari, Edge, Opera */
