const focusOnFirstInputElement = () => {
  document.getElementById("verificationCode").focus();
};


/**
 * If page isn't loaded yet, wait for the page to load, then focus on first
 * input element.
 */
if (document.readyState === "loading") {
  // Loading hasn't finished yet
  document.onreadystatechange = () => {
    if (document.readyState === "complete") {
      focusOnFirstInputElement();
    }
  };
} else {
  // `DOMContentLoaded` has already fired
  focusOnFirstInputElement();
}

verifyBtn.classList.add('disabled');


// Get references to the input fields and the submit button
const verifyInput = document.getElementById('verificationCode');

if (document.readyState === "loading") {
  // Loading hasn't finished yet
  document.addEventListener("DOMContentLoaded", checkInputsUser);
} else {
  // `DOMContentLoaded` has already fired
  checkInputsUser();
}
verifyInput.addEventListener('input', checkInputsUser);



function checkInputsUser() {
  if (verificationCode.value) {
    // If  fields have values, remove the disabled class from the submit button
    verifyBtn.classList.remove('disabled');
  } else {
    // Optional: Add the class back if either field is empty 
    if (!verifyBtn.classList.contains('disabled')) {
      verifyBtn.classList.add('disabled');
    }
  }
}

