  return false;
}

// Pair each variable with its name and the custom string to return if it's true
let options = [
  { name: 'mfaOn', value: mfaOn, returnValue: 'MFA' },
  { name: 'qaOn', value: qaOn, returnValue: 'QA' }, // Update returnValue as needed
  { name: 'standardOn', value: standardOn, returnValue: 'Standard' } // Update returnValue as needed
];

// Filter out the true values
let trueOptions = options.filter(option => option.value);

let showDropdown = false;
let oneOn = null;

if (trueOptions.length > 1) {
  // More than one option is true
  showDropdown = true;
  oneOn = 'multiple';
} else if (trueOptions.length === 1) {
  // Only one option is true
  oneOn = trueOptions[0].returnValue; // Use returnValue for the true option
}

return { showDropdown: showDropdown, oneOn: oneOn };
}

